import React, { useState } from "react";

// Student Dashboard Component
function StudentDashboard() {
  const styles = {
    container: {
      backgroundColor: "white",
      borderRadius: "0.5rem",
      padding: "1.5rem",
      boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0.1)",
    },
    header: {
      marginBottom: "1.5rem",
    },
    statsGrid: {
      display: "grid",
      gridTemplateColumns: "repeat(3, 1fr)",
      gap: "1rem",
      marginBottom: "1.5rem",
    },
    statCard: {
      backgroundColor: "#f3f4f6",
      padding: "1rem",
      borderRadius: "0.5rem",
    },
    activities: {
      backgroundColor: "#f3f4f6",
      padding: "1rem",
      borderRadius: "0.5rem",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={{ fontSize: "1.5rem", fontWeight: "bold" }}>
          Welcome, Student
        </h1>
        <p style={{ color: "#6b7280" }}>Your learning dashboard</p>
      </div>

      <div style={styles.statsGrid}>
        <div style={styles.statCard}>
          <h3 style={{ fontWeight: "bold" }}>Current Progress</h3>
          <p
            style={{ fontSize: "1.5rem", fontWeight: "bold", color: "#2563eb" }}
          >
            85%
          </p>
          <p style={{ color: "#6b7280" }}>In Algebra</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={{ fontWeight: "bold" }}>Tasks Completed</h3>
          <p
            style={{ fontSize: "1.5rem", fontWeight: "bold", color: "#2563eb" }}
          >
            12/15
          </p>
          <p style={{ color: "#6b7280" }}>This Week</p>
        </div>
        <div style={styles.statCard}>
          <h3 style={{ fontWeight: "bold" }}>Next Quiz</h3>
          <p
            style={{ fontSize: "1.5rem", fontWeight: "bold", color: "#2563eb" }}
          >
            2 Days
          </p>
          <p style={{ color: "#6b7280" }}>Geometry Basics</p>
        </div>
      </div>

      <div style={styles.activities}>
        <h2 style={{ fontWeight: "bold", marginBottom: "1rem" }}>
          Recent Activities
        </h2>
        {[
          "Completed Algebra Quiz",
          "Watched Video Tutorial",
          "Solved Practice Problems",
        ].map((activity, index) => (
          <div
            key={index}
            style={{
              padding: "0.75rem",
              backgroundColor: "white",
              marginBottom: "0.5rem",
              borderRadius: "0.375rem",
            }}
          >
            {activity}
          </div>
        ))}
      </div>
    </div>
  );
}

// Teacher Dashboard Component
function TeacherDashboard() {
  const styles = {
    container: {
      backgroundColor: "white",
      borderRadius: "0.5rem",
      padding: "1.5rem",
      boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0.1)",
    },
    header: {
      marginBottom: "1.5rem",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
    },
    button: {
      backgroundColor: "#2563eb",
      color: "white",
      padding: "0.5rem 1rem",
      borderRadius: "0.375rem",
      cursor: "pointer",
    },
    classesGrid: {
      display: "grid",
      gridTemplateColumns: "repeat(2, 1fr)",
      gap: "1rem",
      marginBottom: "1.5rem",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div>
          <h1 style={{ fontSize: "1.5rem", fontWeight: "bold" }}>
            Teacher Dashboard
          </h1>
          <p style={{ color: "#6b7280" }}>Manage your classes</p>
        </div>
        <button style={styles.button}>Add New Class</button>
      </div>

      <div style={styles.classesGrid}>
        {["Math 101", "Algebra II", "Geometry", "Calculus"].map(
          (className, index) => (
            <div
              key={index}
              style={{
                padding: "1rem",
                backgroundColor: "#f3f4f6",
                borderRadius: "0.5rem",
              }}
            >
              <h3 style={{ fontWeight: "bold" }}>{className}</h3>
              <p style={{ color: "#6b7280" }}>25 Students</p>
              <div
                style={{
                  height: "4px",
                  backgroundColor: "#e5e7eb",
                  borderRadius: "2px",
                  marginTop: "0.5rem",
                }}
              >
                <div
                  style={{
                    width: "75%",
                    height: "100%",
                    backgroundColor: "#2563eb",
                    borderRadius: "2px",
                  }}
                />
              </div>
            </div>
          )
        )}
      </div>
    </div>
  );
}

// Assessment Interface Component
function AssessmentInterface() {
  const styles = {
    container: {
      backgroundColor: "white",
      borderRadius: "0.5rem",
      padding: "1.5rem",
      boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0.1)",
    },
    header: {
      marginBottom: "1.5rem",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
    },
    questionContainer: {
      backgroundColor: "#f3f4f6",
      padding: "1.5rem",
      borderRadius: "0.5rem",
      marginBottom: "1.5rem",
    },
    option: {
      padding: "1rem",
      backgroundColor: "white",
      marginBottom: "0.5rem",
      borderRadius: "0.375rem",
      cursor: "pointer",
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div>
          <h1 style={{ fontSize: "1.5rem", fontWeight: "bold" }}>
            Mathematics Assessment
          </h1>
          <p style={{ color: "#6b7280" }}>Question 1 of 10</p>
        </div>
        <div style={{ color: "#6b7280" }}>Time remaining: 25:00</div>
      </div>

      <div style={styles.questionContainer}>
        <p style={{ marginBottom: "1rem" }}>
          Solve the quadratic equation: x² + 5x + 6 = 0
        </p>
        {[
          "x = -2 and x = -3",
          "x = 2 and x = 3",
          "x = -1 and x = -6",
          "x = 1 and x = 6",
        ].map((option, index) => (
          <div key={index} style={styles.option}>
            {option}
          </div>
        ))}
      </div>

      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <button
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#f3f4f6",
            borderRadius: "0.375rem",
          }}
        >
          Previous
        </button>
        <button
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#2563eb",
            color: "white",
            borderRadius: "0.375rem",
          }}
        >
          Next
        </button>
      </div>
    </div>
  );
}

// AI Intervention Component
function AIIntervention() {
  const styles = {
    container: {
      backgroundColor: "white",
      borderRadius: "0.5rem",
      padding: "1.5rem",
      boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0.1)",
    },
    aiMessage: {
      backgroundColor: "#eff6ff",
      padding: "1rem",
      borderRadius: "0.5rem",
      marginBottom: "1rem",
    },
  };

  return (
    <div style={styles.container}>
      <div style={{ marginBottom: "1.5rem" }}>
        <h1 style={{ fontSize: "1.5rem", fontWeight: "bold" }}>
          AI Learning Assistant
        </h1>
        <p style={{ color: "#6b7280" }}>
          Personalized help with your current topic
        </p>
      </div>

      <div style={styles.aiMessage}>
        <h3 style={{ fontWeight: "bold", marginBottom: "0.5rem" }}>
          Suggestion
        </h3>
        <p>
          I notice you're having trouble with quadratic equations. Let's break
          this down into steps:
        </p>
        <ol style={{ marginTop: "0.5rem", marginLeft: "1.5rem" }}>
          <li>First, identify the coefficients</li>
          <li>Use the quadratic formula</li>
          <li>Solve step by step</li>
        </ol>
      </div>

      <div
        style={{
          display: "flex",
          gap: "0.5rem",
        }}
      >
        <button
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#2563eb",
            color: "white",
            borderRadius: "0.375rem",
            flex: 1,
          }}
        >
          Show Example
        </button>
        <button
          style={{
            padding: "0.5rem 1rem",
            backgroundColor: "#f3f4f6",
            borderRadius: "0.375rem",
            flex: 1,
          }}
        >
          Practice More
        </button>
      </div>
    </div>
  );
}

// Collaborative Teaching Component
function CollaborativeTeaching() {
  const styles = {
    container: {
      backgroundColor: "white",
      borderRadius: "0.5rem",
      padding: "1.5rem",
      boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0.1)",
    },
    header: {
      padding: "1rem",
      borderBottom: "1px solid #e5e7eb",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginBottom: "1.5rem",
    },
    mainContent: {
      display: "grid",
      gridTemplateColumns: "repeat(12, 1fr)",
      gap: "1rem",
    },
    toolsSidebar: {
      gridColumn: "span 2",
      display: "flex",
      flexDirection: "column",
      gap: "1rem",
    },
    toolsPanel: {
      backgroundColor: "#f9fafb",
      padding: "1rem",
      borderRadius: "0.5rem",
    },
    toolGrid: {
      display: "grid",
      gridTemplateColumns: "repeat(2, 1fr)",
      gap: "0.5rem",
      marginTop: "0.5rem",
    },
    whiteboard: {
