// Main App Component
function App() {
  const [currentView, setCurrentView] = useState("student");

  const styles = {
    container: {
      padding: "1.5rem",
      backgroundColor: "#f3f4f6",
      minHeight: "100vh",
    },
    nav: {
      display: "flex",
      gap: "1rem",
      marginBottom: "1rem",
    },
    navButton: {
      padding: "0.5rem 1rem",
      borderRadius: "0.375rem",
      cursor: "pointer",
      backgroundColor: "#fff",
      border: "1px solid #e5e7eb",
    },
    activeNavButton: {
      backgroundColor: "#2563eb",
      color: "white",
      border: "none",
    },
  };

  const getView = () => {
    switch (currentView) {
      case "student":
        return <StudentDashboard />;
      case "teacher":
        return <TeacherDashboard />;
      case "collaborative":
        return <CollaborativeTeaching />;
      case "assessment":
        return <AssessmentInterface />;
      case "ai":
        return <AIIntervention />;
      default:
        return <StudentDashboard />;
    }
  };

  return (
    <div style={styles.container}>
      <nav style={styles.nav}>
        {[
          { id: "student", label: "Student Dashboard" },
          { id: "teacher", label: "Teacher Dashboard" },
          { id: "collaborative", label: "Collaborative Teaching" },
          { id: "assessment", label: "Assessment" },
          { id: "ai", label: "AI Intervention" },
        ].map((view) => (
          <button
            key={view.id}
            style={{
              ...styles.navButton,
              ...(currentView === view.id ? styles.activeNavButton : {}),
            }}
            onClick={() => setCurrentView(view.id)}
          >
            {view.label}
          </button>
        ))}
      </nav>
      {getView()}
    </div>
  );
}

export default App;
